# Función para imprimir el array4d en el formato solicitado
def imprimir_array4d(array):
    index = 0  # Índice para recorrer el array
    for asignatura in range(1, asignaturas + 1):
        print(f"ASIGNATURA({asignatura})")
        print("-" * 13)
        for bloque in range(1, bloques + 1):
            print(f"BLOQUE({bloque})")
            for dia in range(1, dias + 1):
                # Extraer la sublista correspondiente a cada DIA y SALA
                sala_values = array[index:index + salas]
                # Formatear la salida como lista
                print(f"    DIA({dia}): {sala_values}")
                # Avanzar el índice por cada conjunto de 10 valores de la sala
                index += salas
            print()  # Salto de línea después de cada bloque
        print()  # Salto de línea después de cada asignatura

# Llamamos a la función con el array
imprimir_array4d(array)